import React, { Component } from "react";
import { Button, FormControl, InputLabel, Input, Grid } from '@material-ui/core/FormControl';
import './styles.css';

export class FormPersonalDetails extends Component {
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    };

    back = e => {
        e.preventDefault();
        this.props.prevStep();
    };

    render() {
        const { values, handleChange } = this.props;
        return (

            <React.Fragment>
                <form id="form-login" className="col" >
                    <FormControl
                        fullWidth
                        className="form-group row">
                        <InputLabel>Username</InputLabel>

                        <Input
                            id="username"
                            type="username"
                            name="username"
                            placeholder="choose a username"
                            onChange={handleChange("username")}
                            defaultValue={values.username}
                        />
                    </FormControl>
                    <FormControl
                        fullWidth
                        className="form-group row">
                        <InputLabel>Location</InputLabel>

                        <Input
                            id="city"
                            type="city"
                            name="city"
                            placeholder="location"
                            onChange={handleChange("city")}

                        />
                    </FormControl>
                    <FormControl
                        fullWidth
                        className="form-group row">
                        <InputLabel>Team</InputLabel>

                        <Input
                            id="team"
                            type="team"
                            name="team"
                            placeholder="which team you support"
                            onChange={handleChange("team")}

                        />
                    </FormControl>

                    <div className="row">
                        <Grid className="list-inline">
                            <Button type="submit" variant="extendedFab" className="btn-success" onClick={this.continue}>
                                next
								</Button>

                            <Button type="submit" variant="extendedFab" className="btn-success" onClick={this.back}>
                                back
								</Button>
                        </Grid>
                    </div>
                </form>
            </React.Fragment>

        );
    }
}


export default FormPersonalDetails;
