import React, { Component } from "react";
import MuiThemeProvider from "material-ui/styles/MuiThemeProvider";
import TextField from "material-ui/TextField";
import RaisedButton from "material-ui/RaisedButton";

import Grid from '@material-ui/core/Grid';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';

export class FormPersonalDetails extends Component {
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    };

    back = e => {
        e.preventDefault();
        this.props.prevStep();
    };

    render() {
        const { values, handleChange } = this.props;
        return (
            <MuiThemeProvider>
                <React.Fragment>
                    <form id="form-login" className="col" >

                        <FormControl
                            fullWidth
                            style={{ color: "red" }}
                            className={`form-group row ${this.errorClass(this.state.formErrors.email)}`}>
                            <InputLabel>Location</InputLabel>

                            <Input
                                id="city"
                                type="city"
                                name="city"
                                placeholder="location"
                                onChange={handleChange("city")}

                            />
                        </FormControl>
                        <FormControl
                            fullWidth
                            style={{ color: "red" }}
                            className={`form-group row ${this.errorClass(this.state.formErrors.email)}`}>
                            <InputLabel>Team</InputLabel>

                            <Input
                                id="city"
                                type="city"
                                name="city"
                                placeholder="which team you support"
                                onChange={handleChange("team")}

                            />
                        </FormControl>

                        <div className="row">
                            <Grid item xs={12}>
                                <button type="submit" variant="extendedFab" className="btn-success" onClick={this.continue} primary={true}>
                                    continue
								</button>

                                <button type="submit" variant="extendedFab" className="btn-success" onClick={this.back} primary={false}>
                                    back
								</button>
                            </Grid>
                        </div>
                    </form>
                </React.Fragment>
            </MuiThemeProvider>
        );
    }
}

const styles = {
    button: {
        margin: 45
    }
};

export default FormPersonalDetails;
